mapper.update:
/*kr.kosmo.jobkorea.pcs.dao.PcsDao*/
UPDATE tb_scm_purchase_list
   SET purch_date = #{purch_date},
       desired_delivery_date = #{desired_delivery_date},
       purch_mng_id = #{purch_mng_id}
 WHERE purch_list_no = #{purch_list_no};

mapper.select:
/*kr.kosmo.jobkorea.pcs.dao.PcsDao*/ SELECT pl.purch_list_no ,pl.order_cd ,sp.supply_nm ,pd.prod_nm ,mc.m_ct_nm ,pd.m_ct_cd ,pl.purch_qty ,wh.warehouse_nm ,DATE_FORMAT(pl.purch_date, "%Y-%m-%d") as purch_date ,DATE_FORMAT(pl.desired_delivery_date, "%Y-%m-%d") as desired_delivery_date ,od.STTcd ,pl.supply_cd ,dc.detail_name ,pd.product_cd FROM tb_scm_purchase_list pl inner join tb_scm_supply sp on sp.supply_cd = pl.supply_cd inner join tb_order od on od.order_cd = pl.order_cd inner join tb_scm_product pd on pd.product_cd = od.product_cd inner join tb_scm_m_category mc on mc.m_ct_cd = pd.m_ct_cd inner join tb_scm_warehouse wh on wh.warehouse_cd = sp.warehouse_cd inner join ( SELECT detail_code, group_code, detail_name FROM tb_detail_code WHERE group_code = 'STTcd' ) dc ON dc.detail_code = od.STTcd WHERE od.STTcd in (10,12)
and (od.order_cd LIKE CONCAT('%', #{sname}, '%') or pd.m_ct_cd LIKE CONCAT('%', #{sname}, '%') or pd.prod_nm LIKE CONCAT('%', #{sname}, '%'))
and pd.m_ct_cd LIKE CONCAT('%', #{sname}, '%')
and pd.prod_nm LIKE CONCAT('%', #{sname}, '%')
and ( DATE_FORMAT(pl.purch_date, "%Y-%m-%d") = #{date} or DATE_FORMAT(pl.desired_delivery_date, "%Y-%m-%d") = #{date} )
ORDER BY purch_list_no DESC LIMIT #{pageIndex}, #{pageSize}

mapper.update:
/*kr.kosmo.jobkorea.pcs.dao.PcsDao*/
UPDATE tb_order
   SET STTcd = 12
 WHERE order_cd = #{order_cd} AND STTcd = 10;
