mapper.select:
SELECT name
FROM tb_userinfo
WHERE loginId = #{loginId}

mapper.update:
UPDATE tb_order
SET STTcd = #{STTcd}
WHERE order_cd = #{orderCode}

mapper.insert:
INSERT INTO tb_scm_ship_list (
								order_cd,
								scm_id,
								submit_date
 		                      )
VALUES (
			#{orderCode},
			#{loginId},
			#{submitDate}
		)
